sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
 //	alert("xxx111" + newValue);
  if (newValue === '') {
      return;
   }

	//alert("xxx" + newValue);

	var what_needed = "u_what_needed"
	var what_needed_value = g_form.getValue(what_needed)
	g_form.clearOptions(what_needed);
	switch(newValue) {
		case "hr":
			g_form.addOption(what_needed, "hr1", "Human resource 1");
			g_form.addOption(what_needed, "hr3", "Human resource 2");
			break;
		case "legal":
			g_form.addOption(what_needed, "legal1", "legal 1");
			g_form.addOption(what_needed, "legal2", "legal 2");
			break;
		case "build":
			g_form.addOption(what_needed, "build1", "build 1");
			g_form.addOption(what_needed, "build2", "build 2");
			break;
	}
	g_form.addOption(what_needed, "other", "Other");

	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue(what_needed, what_needed_value);
	}
   //Type appropriate comment here, and begin script below

}
